import React, { Component } from 'react';

class TaskList extends Component {
    constructor(props) {
        this.itemList = this.itemList.bind(this);
    }

    itemsList = () =>{

        const itemList = this.props.taskItems;
        const itemsListToShow = itemList.map(item=>{
            return (
                <div>
                    <p>{item.text}</p>
                </div>
            );

        })

        return(
            <div>
                {this.itemsList}
            </div>
        );
    }

    render() { 
        return ( 
            {itemList}
         );
    }
}

export default TaskList;
